import React from "react";
import { Link } from "react-router-dom";
import { connect } from "react-redux";
import { getBooks, getBook, deleteBook } from "../actions";
import "../../../../styles/book-blog/main.scss";

class BookShow extends React.Component {
  componentDidMount() {
    this.props.getBooks();
    this.props.getBook();
    this.mapStateToProps();
  }

  render() {
    console.log(this.props);
    return (
      <div className="main">
        <div className="container d-flex flex-column align-items-center">
          <h1>{this.props.selectedBook.title}</h1>
          <h2>By: blog.author </h2>
          {/* <img src="blog.image"> */}
          <p>blog.desc </p>
        </div>

        <div
          className="container d-flex justify-content-around"
          id="edit-and-delete"
        >
          <a
            className="btn btn-warning btn-lg" /* href="/books/<%= blog._id %>/edit" */
          >
            Edit
          </a>

          {/* <form action="/books/<%= blog._id %>?_method=DELETE" method="POST"> */}
          <input className="btn btn-danger btn-lg" type="submit" value="Delete" />
          {/* </form> */}
        </div>
      </div>
    );
  }
}

const mapStateToProps = (state, ownProps) => {
  return {
    books: Object.values(state.books),
    book: state.books[ownProps.match.params.id]
  };
};

export default connect(mapStateToProps, { getBooks, getBook, deleteBook })(BookShow);
