import React from "react";
import { connect } from "react-redux";
import { Link } from "react-router-dom";
import { getBooks } from "../actions";

import "../../../../styles/book-blog/main.scss";

class BookList extends React.Component {
  componentDidMount() {
    this.props.getBooks();
  }

  renderList = () => {
    return this.props.books.map(book => {
      return (
        <div>
        <div className="container d-flex flex-column align-items-center">
          <h1>{book.title}</h1>
          <h2>{book.author}</h2>

          <img src="`${book.image}`" />

          {/* <a class="btn btn-info" href="/books/<%= blog._id %>">Click for more info...</a> */}
        </div>
        <hr />
        </div>
      );
    });
  };

  render() {
    return (
      // for (let blog of blogs) {
      <div className="main">
        <div>
          <div className="container d-flex flex-column align-items-center">
            <h1> blog.title </h1>
            <h2>By: blog.author </h2>

            {/* <img src=" blog.image " /> */}

            {/* <a class="btn btn-info" href="/books/<%= blog._id %>">Click for more info...</a> */}
          </div>
          <hr />

          <br />
          <Link to="new" className="btn btn-primary" id="new-book">
            Add a new book!
          </Link>
        </div>
      </div>
    );
    // {/* ); */}
  }
}

const mapStateToProps = (state, ownProps) => {
  return {
    books: Object.values(state.recipes)
    // selectedRecipe: state.recipes[ownProps.match.params.id]
  };
};

export default connect(mapStateToProps, { getBooks })(BookList);
